import React from 'react';

export default class InputMeteo extends React.Component {

    constructor(props){
        // console.warn("Constructor")
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            items: ''
        }
        // this.handelChange = this.handelChange.bind(this) ; 
        // this.handelSubmit = this.handelSubmit.bind(this) ;

    }

    // handelChange (e) {
    //     this.setState({ data : e.target.value})
    // }

    // handelSubmit (e){
    //     e.preventDefault() ;
    //     console.log(this.state);
    // }


    componentDidMount() {
        // console.warn("CompodidMount")
        fetch("https://api.openweathermap.org/data/2.5/weather?q=London&appid=7688b4baa29e1a3064e6400b45e142d6")
          .then(res => res.json())
          .then(
            (result) => {

              this.setState({
                isLoaded: true,
                items: result
              });
              console.log(result);
            },
            // Remarque : il est important de traiter les erreurs ici
            // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
            // des exceptions provenant de réels bugs du composant.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }




    // render () {
    //     return (
    //         <React.Fragment>

    //             <h1> Composant InputMeteo</h1>
    //             <p> {this.state.data}  </p>
    //             <form   onSubmit={this.handelSubmit}>
    //                 <input type="text" placeholder='Saisir une location'  value={this.state.data}  onChange={this.handelChange} ></input>
    //                 <input type="submit" value='Rechercher' onClick={this.handelSubmit} ></input>
    //             </form>
    //         </React.Fragment>

    //     )

    // }


    render() {
        // console.warn("Render")
        const { error, isLoaded, items } = this.state;
        if (error) {
          return <div>Erreur : {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Chargement…</div>;
        } else {
          return (
            <div>
                {items} 
            </div>


          )
        }
    }

}
