import React from 'react';

export default class InputMeteo extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            data : '',
            error: null,
            isLoaded: false,
            items: []
        }
        this.handelChange = this.handelChange.bind(this) ; 
        this.handelSubmit = this.handelSubmit.bind(this) ;

    }

    handelChange (e) {
        this.setState({ data : e.target.value})
    }

    handelSubmit (e){
        e.preventDefault() ;

        if (!this.state.data  ||  this.state.data === ''){
            return ;
        }
        console.log(this.state);
    }


    componentDidMount() {
        fetch("https://api.example.com/items")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            // Remarque : il est important de traiter les erreurs ici
            // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
            // des exceptions provenant de réels bugs du composant.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }




    render () {
        return (
            <React.Fragment>

                <h1> Composant InputMeteo</h1>
                <p> {this.state.data}  </p>
                <form   onSubmit={this.handelSubmit}>
                    <input type="text" placeholder='Saisir une location'  value={this.state.data}  onChange={this.handelChange} ></input>
                    <input type="submit" value='Rechercher' onClick={this.handelSubmit} ></input>
                </form>
            </React.Fragment>

        )

    }

}
