import React from 'react';

export default class InputMeteo extends React.Component {

    constructor(props){
        // console.warn("Constructor")
        super(props)
        this.state = {
            data: '' ,
            error: null,
            isLoaded: false,
            items: ''
        }
        // this.handelChange = this.handelChange.bind(this) ; 
        // this.handelSubmit = this.handelSubmit.bind(this) ;

    }

    // handelChange (e) {
