    componentDidMount() {
        // console.warn("CompodidMount")
        fetch("https://api.openweathermap.org/data/2.5/weather?q=" + data+ "&appid=7688b4baa29e1a3064e6400b45e142d6")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result
              });
            },
            // Remarque : il est important de traiter les erreurs ici
            // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
            // des exceptions provenant de réels bugs du composant.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }




    // render () {
    //     return (
