    //                 <input type="submit" value='Rechercher' onClick={this.handelSubmit} ></input>
    //             </form>
    //         </React.Fragment>

    //     )

    // }


    render() {
        // console.warn("Render")
        const { error, isLoaded, items } = this.state;
        if (error) {
          return <div>Erreur : {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Chargement…</div>;
        } else {
          return (



            <React.Fragment>
                <h1> Composant InputMeteo</h1>
                {items.name} 

                    <form   onSubmit={this.handelSubmit}>
                    <input type="text" placeholder='Saisir une location'  value={this.state.data}  onChange={this.handelChange} ></input>
                    <input type="submit" value='Rechercher' onClick={this.handelSubmit} ></input>
                </form>
            </React.Fragment>


          )
        }
    }

}
