import React from 'react';

class InputMeteo extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            data : 'MAMACITA'
        }
        this.handelChange = this.handelChange.bind(this)

    }

    render () {
        return (
            <React.Fragment>

                <h1> Composant InputMeteo</h1>
                <p> {this.state.data}  </p>
                <input type="text" placeholder='Saisir une location'  onChange={this.handelChange} ></input>
                <input type="submit" value='Rechercher' ></input>
            </React.Fragment>

        )

    }

}

export default InputMeteo ;
